const sections=document.querySelectorAll("section"),navLi=document.querySelectorAll(".nav__item");window.addEventListener("scroll",(()=>{let e="";sections.forEach((t=>{const n=t.offsetTop,s=t.clientHeight;pageYOffset>=n-s/3&&(e=t.getAttribute("id"))})),navLi.forEach((t=>{t.classList.remove("active"),t.classList.contains(e)&&t.classList.add("active")}))}));const navMenu=document.getElementById("nav-menu"),toggleMenu=document.getElementById("toggle-menu"),closeMenu=document.getElementById("close-menu");toggleMenu.addEventListener("click",(()=>{navMenu.classList.toggle("show")})),closeMenu.addEventListener("click",(()=>{navMenu.classList.remove("show")}));